def  eliminar_repetidos(temas,k):
    if(k==len(temas)-1):
        return temas
    else:
        if(temas[k][0]==temas[k+1][0] and temas[k][1]==temas[k+1][1] and temas[k][2]==temas[k+1][2] and temas[k][3]==temas[k+1][3]):
            temas.pop(k+1)
            eliminar_repetidos(temas,k)
        else:
            eliminar_repetidos(temas,k+1)
